@font-face {
        font-family: "Lato";
        font-weight: 700;
        font-style: normal;
        src: url("/static/fonts/lato/lato-bol-webfont-g.eot");
        src: url("/static/fonts/lato/lato-bol-webfont-g.eot?#iefix") format("embedded-opentype"),
        url("/static/fonts/lato/lato-bol-webfont-g.woff") format("woff"),
        url("/static/fonts/lato/lato-bol-webfont-g.ttf") format("truetype"),
        url("/static/fonts/lato/lato-bol-webfont.svg#latobold") format("svg");
      }

      * {
        padding: 0;
        margin: 0;
        border: none;
        background: none;
        box-shadow: none;
        outline: none;
      }

      body {
        height: 100%;
        font-size: 12px;
        font-family: "Lato", Helvetica, Arial, sans-serif;
        color: #585858;
        background-color: #fff;
      }

      .noscript {
        display: block !important;
        background-color: yellow;
        position: fixed;
        z-index: 9999;
        text-align: center;
        top: 0;
        width: 100%;
        height: 60px;
        font-size: 18px;
        color: #ff8800;
        line-height: 60px;
        border-bottom: 1px solid #ebebeb;
      }

      .v-middle {
        vertical-align: middle;
      }

      .noscript img {
        margin-right: 18px;
      }

      .noscript span {
        font-weight: bold;
        font-size: 20px;
      }



