 and SDKs&q;,&q;items&q;:[],&q;level&q;:1,&q;icon&q;:&q;sdks&q;,&q;file&q;:&q;sdks/&q;,&q;link&q;:&q;sdks&q;},{&q;type&q;:&q;leaf&q;,&q;title&q;:&q;RingCentral Labs&q;,&q;items&q;:[],&q;level&q;:1,&q;icon&q;:&q;ringcentral-labs&q;,&q;file&q;:&q;https://www.ringcentral.com/apps/p/ringcentral-labs&q;,&q;external&q;:true,&q;link&q;:&q;https://www.ringcentral.com/apps/p/ringcentral-labs&q;}]}],&q;page&q;:{&q;lastUpdated&q;:&q;2024-08-15&q;,&q;contributors&q;:[{&q;name&q;:&q;Byrne Reese&q;,&q;link&q;:&q;https://github.com/byrnereese&q;,&q;image&q;:&q;https://avatars.githubusercontent.com/u/28429?v=4&a;size=32&q;},{&q;name&q;:&q;None&q;,&q;link&q;:&q;https://github.com/singhgupi&q;,&q;image&q;:&q;https://avatars.githubusercontent.com/u/58199964?v=4&a;size=32&q;},{&q;name&q;:&q;None&q;,&q;link&q;:&q;https://github.com/pbmacintyre&q;,&q;image&q;:&q;https://avatars.githubusercontent.com/u/5166519?v=4&a;size=32&q;},{&q;name&q;:&q;Michael Stowe&q;,&q;link&q;:&q;https://github.com/mikestowe&q;,&q;image&q;:&q;https://avatars.githubusercontent.com/u/414978?v=4&a;size=32&q;},{&q;name&q;:&q;John Wang&q;,&q;link&q;:&q;https://github.com/grokify&q;,&q;image&q;:&q;https://avatars.githubusercontent.com/u/209872?v=4&a;size=32&q;},{&q;name&q;:&q;Paco Vu&q;,&q;link&q;:&q;https://github.com/PacoVu&q;,&q;image&q;:&q;https://avatars.githubusercontent.com/u/13070584?v=4&a;size=32&q;},{&q;name&q;:&q;vincent.cai&q;,&q;link&q;:&q;https://github.com/vincent-cai&q;,&q;image&q;:&q;https://avatars.githubusercontent.com/u/40990636?v=4&a;size=32&q;},{&q;name&q;:&q;Victor Shisterov&q;,&q;link&q;:&q;https://github.com/vshisterov&q;,&q;image&q;:&q;https://avatars.githubusercontent.com/u/10144418?v=4&a;size=32&q;},{&q;name&q;:&q;Karina Gusmanova&q;,&q;link&q;:&q;https://github.com/kerireki&q;,&q;image&q;:&q;https://avatars.githubusercontent.com/u/15921044?v=4&a;size=32&q;},{&q;name&q;:&q;Craig Cook&q;,&q;link&q;:&q;https://github.com/craigco&q;,&q;image&q;:&q;https://avatars.githubusercontent.com/u/5821668?v=4&a;size=32&q;}],&q;editOnGitHub&q;:&q;https://github.com/ringcentral/ringcentral-api-docs/tree/master/docs/index.md&q;,&q;docTitle&q;:&q;Developing with RingCentral&q;,&q;title&q;:&q;RingCentral RingEX™ Developer Guide&q;,&q;breadcrumb&q;:[],&q;isRatingEnabled&q;:true,&q;toc&q;:[{&q;title&q;:&q;RingCentral RingEX™ Developer Guide&q;,&q;link&q;:&q;ringcentral-ringextm-developer-guide&q;,&q;level&q;:&q;1&q;,&q;items&q;:[{&q;title&q;:&q;RingCentral Embeddable 2.0 is here&q;,&q;link&q;:&q;ringcentral-embeddable-20-is-here&q;,&q;level&q;:&q;2&q;,&q;type&q;:&q;leaf&q;,&q;active&q;:false},{&q;title&q;:&q;Popular use cases&q;,&q;link&q;:&q;popular-use-cases&q;,&q;level&q;:&q;2&q;,&q;type&q;:&q;leaf&q;,&q;active&q;:false},{&q;title&q;:&q;Check out these popular developer topics&q;,&q;link&q;:&q;check-out-these-popular-developer-topics&q;,&q;level&q;:&q;2&q;,&q;type&q;:&q;leaf&q;,&q;active&q;:false},{&q;title&q;:&q;Explore our APIs&q;,&q;link&q;:&q;explore-our-apis&q;,&q;level&q;:&q;2&q;,&q;type&q;:&q;leaf&q;,&q;active&q;:false},{&q;title&q;:&q;Getting Help&q;,&q;link&q;:&q;getting-help&q;,&q;level&q;:&q;2&q;,&q;type&q;:&q;leaf&q;,&q;active&q;:false},{&q;title&q;:&q;Resources&q;,&q;link&q;:&q;resources&q;,&q;level&q;:&q;2&q;,&q;type&q;:&q;leaf&q;,&q;active&q;:false},{&q;title&q;:&q;About RingCentral&q;,&q;link&q;:&q;about-ringcentral&q;,&q;level&q;:&q;2&q;,&q;type&q;:&q;leaf&q;,&q;active&q;:false}],&q;type&q;:&q;node&q;}],&q;meta&q;:[],&q;content&q;:&q;\n\n\n\n\n&l;div class=\&q;admonition warning\&q;&g;\n&l;p class=\&q;admonition-title\&q;&g;Important announcement regarding the RingCentral sandbox&l;/p&g;\n&l;p&g;In our ongoing effort to simplify the RingCentral Developer experience, we are in the process of &l;a href=\&q;https://community.ringcentral.com/developer-platform-apis-integrations-5/streamlining-the-application-development-process-at-ringcentral-and-bidding-farewell-to-sandbox-9605\&q; target=\&q;_blank\&q;&g;phasing out the sandbox environment&l;/a&g;. During the process and to support developers who need to migrate away, &l;a href=\&q;/guide/basics/using-sandbox\&q;&g;sandbox&l;/a&g; will remain available. &l;/p&g;\n&l;/div&g;\n&l;div class=\&q;container col-xxl-12 px-4 py-2\&q;&g;\n    &l;div class=\&q;row flex-lg-row-reverse align-items-center py-2\&q;&g;\n      &l;div class=\&q;col-10 col-sm-8 col-lg-6\&q;&g;\n        &l;img src=\&q;https://assets-developers.ringcentral.com/dpw/guide/images/embeddable-phone-mini.png?v=2024-10-18\&q; class=\&q;d-block mx-lg-auto img-fluid\&q; alt=\&q;RingCentral Embeddable phone and javascript widget\&q; loading=\&q;lazy\&q;&g;\n      &l;/div&g;\n      &l;div class=\&q;col-lg-6\&q;&g;\n        &l;h2 class=\&q;display-5 fw-bold lh-1 mb-3 heading-ringcentral-embeddable-20-is-here\&q; id=\&q;ringcentral-embeddable-20-is-here\&q;&g;RingCentral Embeddable 2.0 is here&l;/h2&g;\n        &l;p class=\&q;lead\&q;&g;RingCentral Embeddable is a fully-functional communications client you can embed into any webpage. Use it to integrate RingCentral into your application, or to build you own unique communications experience. New features include:&l;/p&g;\n        &l;ul&g;\n           &l;li&g;New design&l;/li&g;\n           &l;li&g;&l;a href=\&q;https://ringcentral.github.io/ringcentral-embeddable/docs/integration/customized-page/\&q; target=\&q;_blank\&q;&g;Custom pages and tabs&l;/a&g;&l;/li&g;\n           &l;li&g;&l;a href=\&q;https://ringcentral.github.io/ringcentral-embeddable/docs/integration/custom-settings/\&q; target=\&q;_blank\&q;&g;Custom settings&l;/a&g;&l;/li&g;\n           &l;li&g;Noise reduction&l;/li&g;\n           &l;li&g;Call recording playback&l;/li&g;\n           &l;li&g;And more.&l;/li&g;\n        &l;/ul&g;\n        &l;div class=\&q;d-grid gap-2 d-md-flex justify-content-md-start\&q;&g;\n          &l;a href=\&q;https://ringcentral.github.io/ringcentral-embeddable/docs/2.x/\&q; class=\&q;btn btn-primary btn-lg px-4 me-md-2\&q; target=\&q;_blank\&q;&g;Get started&l;/a&g;\n        &l;/div&g;\n      &l;/div&g;\n    &l;/div&g;\n  &l;/div&g;\n\n&l;h2 id=\&q;popular-use-cases\&q; class=\&q;heading-popular-use-cases\&q;&g;Popular use cases&l;/h2&g;\n&l;div class=\&q;row row-cols-1 row-cols-md-3 mb-3\&q;&g;\n&l;div class=\&q;col\&q;&g;\n&l;div class=\&q;card bg-light bg-gradient h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h3 card-title\&q;&g;Send and receive SMS messages&l;/h5&g;\n      &l;h6 class=\&q;h4 card-subtitle mt-0 mb-2\&q;&g;Stay connected and engaged with customers at scale through messaging automation.&l;/h6&g;\n      &l;p class=\&q;card-text\&q;&g;Use RingCentral to send and receive SMS, and access your SMS message history.&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4 pb-2\&q;&g;\n        &l;li&g;&l;a href=\&q;/guide/messaging/sms/sending-sms\&q; class=\&q;card-link\&q;&g;Send an SMS&l;/a&g;&l;/li&g;\n        &l;li&g;&l;a href=\&q;/guide/messaging/sms/sending-images\&q; class=\&q;card-link\&q;&g;Send images over SMS&l;/a&g;&l;/li&g;\n        &l;li&g;&l;a href=\&q;/guide/messaging/sms/high-volume/sending-highvolume-sms\&q; class=\&q;card-link\&q;&g;Send High Volume SMS&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n&l;/div&g;\n&l;/div&g;\n&l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card bg-light bg-gradient h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h3 card-title\&q;&g;Synchronize call history&l;/h5&g;\n      &l;h6 class=\&q;h4 card-subtitle mt-0 mb-2\&q;&g;Download and process your entire company&a;apos;s call history for compliance and analytics.&l;/h6&g;\n      &l;p class=\&q;card-text\&q;&g;Learn how to access your company&a;apos;s communication history:&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4 pb-2\&q;&g;\n        &l;li&g;&l;a href=\&q;/guide/voice/call-log/details\&q; class=\&q;card-link\&q;&g;Trace calls flowing across the network&l;/a&g;&l;/li&g;\n        &l;li&g;&l;a href=\&q;/guide/voice/call-log/recordings\&q; class=\&q;card-link\&q;&g;Access call recordings&l;/a&g;&l;/li&g;\n        &l;li&g;&l;a href=\&q;/guide/voice/call-log/archival\&q; class=\&q;card-link\&q;&g;Archive call histories&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n&l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card bg-light bg-gradient h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h3 card-title\&q;&g;Analyze call and meeting recordings&l;/h5&g;\n      &l;h6 class=\&q;h4 card-subtitle mt-0 mb-2\&q;&g;Archive and extract key data and insights from call recordings&l;/h6&g;\n      &l;p class=\&q;card-text\&q;&g;RingCentral securely stores all media you generate and makes it available to you through an API.&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4 pb-2\&q;&g;\n        &l;li&g;&l;a href=\&q;/guide/voice/call-log/recordings\&q; class=\&q;card-link\&q;&g;Access call recordings&l;/a&g;&l;/li&g;\n        &l;li&g;&l;a href=\&q;/guide/video/api/meeting-history\&q; class=\&q;card-link\&q;&g;Access meeting recordings&l;/a&g;&l;/li&g;\n        &l;li&g;&l;a href=\&q;/guide/messaging/message-store/working-with-message-store\&q; class=\&q;card-link\&q;&g;Access voicemails&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n&l;/div&g;\n&l;h2 id=\&q;check-out-these-popular-developer-topics\&q; class=\&q;heading-check-out-these-popular-developer-topics\&q;&g;Check out these popular developer topics&l;/h2&g;\n&l;div class=\&q;row row-cols-1 row-cols-md-2 mb-3\&q;&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;Authentication&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;Every app needs to authenticate to the RingCentral platform. Learn what auth method is best for your app.&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4\&q;&g;\n      &l;li&g;&l;a href=\&q;/guide/authentication/jwt/quick-start\&q; class=\&q;card-link\&q;&g;JWT is great for server-to-server apps&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;/guide/authentication/quick-start\&q; class=\&q;card-link\&q;&g;OAuth is ideal for apps with a user interface&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;/guide/authentication/refresh-tokens\&q; class=\&q;card-link\&q;&g;Refresh tokens keep sessions active&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;Webhooks and events&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;Get notified when key events occur so that your app can respond to events in real-time.&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4\&q;&g;\n      &l;li&g;&l;a href=\&q;/guide/notifications/webhooks/creating-webhooks\&q; class=\&q;card-link\&q;&g;Create a webhook&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;/guide/notifications/push-notifications/pubnub\&q; class=\&q;card-link\&q;&g;Push notifications are great for pure client-side apps&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;https://developers.ringcentral.com/api-reference/Account-Presence-Event\&q; class=\&q;card-link\&q; target=\&q;_blank\&q;&g;See a list of events you can subscribe to&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n&l;/div&g;\n\n&l;h2 id=\&q;explore-our-apis\&q; class=\&q;heading-explore-our-apis\&q;&g;Explore our APIs&l;/h2&g;\n&l;div class=\&q;row row-cols-1 row-cols-md-4 mb-3\&q;&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;Artificial intelligence&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;Generate transcripts and extract conversion insights from any media file.&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4\&q;&g;\n        &l;li&g;&l;a href=\&q;/guide/ai/speech-to-text\&q; class=\&q;card-link\&q;&g;Create a transcript&l;/a&g;&l;/li&g;\n        &l;li&g;&l;a href=\&q;/guide/ai/speaker-diarization\&q; class=\&q;card-link\&q;&g;Know who spoke and when&l;/a&g;&l;/li&g;\n        &l;li&g;&l;a href=\&q;/guide/ai/text-summary\&q; class=\&q;card-link\&q;&g;Generate a summary of a conversation&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;Team messaging&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;Use RingCentral to post interactive messages so users can get more work done without leaving RingCentral.&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4\&q;&g;\n      &l;li&g;&l;a href=\&q;/guide/team-messaging/add-ins/creation\&q; class=\&q;card-link\&q;&g;Build an add-in&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;/guide/team-messaging/adaptive-cards\&q; class=\&q;card-link\&q;&g;Learn about Adaptive Cards&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;Voice and telephony&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;Use RingCentral to enable a &a;quot;click-to-dial&a;quot; experience, and to manage calls in progress.&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4\&q;&g;\n      &l;li&g;&l;a href=\&q;/guide/voice/ringout\&q; class=\&q;card-link\&q;&g;Place a call with RingOut&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;/guide/voice/call-control\&q; class=\&q;card-link\&q;&g;Manipulate active calls&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;/guide/voice/call-log/recordings\&q; class=\&q;card-link\&q;&g;Access call recordings&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;Fax&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;Use RingCentral to manage the faxes your company sends and receives.&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4\&q;&g;\n      &l;li&g;&l;a href=\&q;/guide/messaging/fax/sending-faxes\&q; class=\&q;card-link\&q;&g;Send a fax&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;/guide/messaging/fax/receiving-faxes\&q; class=\&q;card-link\&q;&g;Receive a fax&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n&l;/div&g;\n\n&l;div class=\&q;row row-cols-1 row-cols-md-4 mb-3\&q;&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;Meetings and video&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;Schedule meetings and access meeting history and recordings.&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4\&q;&g;\n      &l;li&g;&l;a href=\&q;/guide/meetings/quick-start\&q; class=\&q;card-link\&q;&g;Schedule a meeting&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;https://developers.ringcentral.com/api-reference/Meeting-Recordings/listAccountMeetingRecordings\&q; class=\&q;card-link\&q; target=\&q;_blank\&q;&g;Access meeting recordings&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;Analytics&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;Extract meaning insights into one&a;apos;s usage of the RingCentral communications platform.&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4\&q;&g;\n      &l;li&g;&l;a href=\&q;/guide/analytics/aggregate\&q; class=\&q;card-link\&q;&g;See the number of calls made, and time spent on the phone&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;/guide/analytics/timeline\&q; class=\&q;card-link\&q;&g;See call analytics broken down by time&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;Webinar&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;Automate webinar messaging and integrate with your CRM.&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4\&q;&g;\n        &l;li&g;&l;a href=\&q;/guide/webinar/quick-start\&q; class=\&q;card-link\&q;&g;Get started&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body pt-0 pb-0\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;Call handling&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;Manage active calls as they are happening in real-time&l;/p&g;\n      &l;ul class=\&q;pl-0 ml-4\&q;&g;\n      &l;li&g;&l;a href=\&q;/guide/voice/call-routing\&q; class=\&q;card-link\&q;&g;Route calls to the right place&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;/guide/voice/call-routing/manual/call-queues\&q; class=\&q;card-link\&q;&g;Manage call queues&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;/guide/voice/call-control\&q; class=\&q;card-link\&q;&g;Manipulate active calls&l;/a&g;&l;/li&g;\n      &l;li&g;&l;a href=\&q;/guide/voice/supervision\&q; class=\&q;card-link\&q;&g;Supervise calls&l;/a&g;&l;/li&g;\n      &l;/ul&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n&l;/div&g;\n\n&l;h2 id=\&q;getting-help\&q; class=\&q;heading-getting-help\&q;&g;Getting Help&l;/h2&g;\n&l;p&g;If on your way to building your first RingCentral application you encounter difficulty or need help, we are here to assist. Here are our most popular support resources available to you:&l;/p&g;\n&l;ul&g;\n&l;li&g;&l;a href=\&q;https://devcommunity.ringcentral.com/\&q; target=\&q;_blank\&q;&g;Developer Forums&l;/a&g; - post a question to our support community.&l;/li&g;\n&l;li&g;&l;a href=\&q;http://stackoverflow.com/questions/tagged/ringcentral\&q; target=\&q;_blank\&q;&g;StackOverflow&l;/a&g; - seek help from one of the Internet&a;apos;s most popular Q&a;amp;A sites for developers.&l;/li&g;\n&l;li&g;&l;a href=\&q;https://developer.ringcentral.com/community.html\&q; target=\&q;_blank\&q;&g;Live Chat&l;/a&g; - post your question to our public Glip Team for live, real-time support during business hours.&l;/li&g;\n&l;/ul&g;\n&l;h2 id=\&q;resources\&q; class=\&q;heading-resources\&q;&g;Resources&l;/h2&g;\n&l;div class=\&q;row row-cols-1 row-cols-md-2 mb-3\&q;&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;API Reference&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;Consult our exhaustive API Reference Guide, and make API call using ZERO CODE.&l;/p&g;\n      &l;a href=\&q;https://developer.ringcentral.com/api-reference\&q; class=\&q;btn btn-primary\&q; target=\&q;_blank\&q;&g;Learn more&l;/a&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n  &l;div class=\&q;col\&q;&g;\n\n  &l;div class=\&q;card h-100\&q;&g;\n    &l;div class=\&q;card-body\&q;&g;\n      &l;h5 class=\&q;h5 card-title\&q;&g;SDKs&l;/h5&g;\n      &l;p class=\&q;card-text\&q;&g;We offer development libraries in a number of languages (C#, Java, Python, PHP, Javascript and more) to made building apps easier.&l;/p&g;\n      &l;a href=\&q;https://developer.ringcentral.com/library/sdks.html\&q; class=\&q;btn btn-primary\&q; target=\&q;_blank\&q;&g;Learn more&l;/a&g;\n    &l;/div&g;\n  &l;/div&g;\n\n  &l;/div&g;\n&l;/div&g;\n\n&l;h2 id=\&q;about-ringcentral\&q; class=\&q;heading-about-ringcentral\&q;&g;About RingCentral&l;/h2&g;\n&l;p&g;RingCentral is a leading provider of global enterprise cloud communications and collaboration solutions. More flexible and cost-effective than legacy on-premises systems, RingCentral empowers modern mobile and distributed workforces to communicate, collaborate, and connect from any location, on any device and via any mode. RingCentral provides unified voice, video, team messaging and collaboration, conferencing, online meetings, digital customer engagement and integrated contact center solutions for enterprises globally. RingCentral&a;#x2019;s open platform integrates with leading business apps and enables customers to easily customize business workflows.&l;/p&g;\n\n\n&q;}}}
